import axios from "axios";


export const axiosWithAuth = () => {
    const getToken = () => {
        return localStorage.getItem("token");
      }
	// Return an instance of axios
	return axios.create({
		baseURL : 'http://localhost:5000',
		headers : {
			Authorization : getToken()
		},
	});
};
